    const { routes } = props;
    const { Footer } = Layout;

    return (
        <Row>
            <Col lg={4} />
            <Col lg={16}>
                <MenuTop />
                <LoadRoutes routes={routes} />
                <Footer>CÃ©sar Val Leiva</Footer>
            </Col>
            <Col lg={4} />
        </Row>
